mapper.update:
update sys_user set isdelete=1 where user_id in 

	#{userId}

;
delete from sys_user_role where user_id in 

	#{userId}

mapper.select:
select count(1) from ( SELECT aa.user_id as id, aa.username, aa.phone , aa.email , authorize_code.auth_code as authoryCode, authorize_code.authorize_data as authoryTime FROM( SELECT sys_user.user_id , sys_user.username , sys_user.phone , sys_user.email , sys_user.create_time, user_code.auth_id FROM sys_user LEFT JOIN user_code ON sys_user.user_id = user_code.user_id WHERE sys_user.isdelete=0 AND sys_user.unit_id = #{unitId} and sys_user.user_id != #{parentId} ORDER BY sys_user.create_time DESC )as aa LEFT JOIN authorize_code on aa.auth_id=authorize_code.auth_id )as bb
WHERE bb.username LIKE CONCAT('%',#{account},'%')
WHERE bb.phone =#{phone}
limit #{currentPage}, #{pagesize}

mapper.delete:
delete from authorize_code where unit_id =#{unitId};
delete from user_code where user_id in (select user_id from sys_user where user_id =#{userId} or parent_id =#{userId});
delete from sys_user_role where user_id in (select user_id from sys_user where user_id = #{userId} or parent_id =#{userId})
